import React, { useEffect, useState } from "react";
import styled from "styled-components";
import {
  getFireBase,
  getFirestore,
  getDocs,
  collection,
  doc,
  addDoc,
} from "firebase/firestore";
import Header from "./components/layout/Header";
import Footer from "./components/layout/Footer";
import { db } from "./firebaseconfig";

const Site = styled.div`
  width: auto;
  height: auto;
  background: #fffcf6;
`;

const TextContainer = styled.div`
  text-align: center;
  font-size: 48px;
  font-weight: bold;
  color: white;
  -webkit-text-stroke: 0.75px #c4be9f;
`;

const Container = styled.div`
  margin: 5vh 5vh 5vw 35vw;
  width: 400px;
  height: auto;
  background: rgb(137, 215, 169);
  background: linear-gradient(
    180deg,
    rgba(137, 215, 169, 1) 0%,
    rgba(84, 193, 129, 1) 100%
  );
  border-radius: 15px;
  border: 1.5px solid #88665d;
`;

const ContainerItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  column-gap: 40px;
  width: auto;
  height: auto;
`;

const TextContainerItem = styled.div`
  margin: 0px 0px 15px 15px;
  text-align: center;
  font-size: 20px;
  font-weight: bold;
  color: white;
  -webkit-text-stroke: 0.75px #c4be9f;
`;

const FormBlock = styled.input`
  overflow: hidden;
  margin: 13px 15px 25px 0px;
  padding: 2px;
  height: 32px;
  width: 240px;
  color: grey;
  background-color: white;
  border: 1px solid grey;
  border-radius: 15px;
  &:hover {
    cursor: text;
  }
`;

const TextAreaBlock = styled.div`
  overflow: hidden;
  margin: 13px 15px 0px 0px;
  padding: 2px;
  height: auto;
  width: 240px;
  
  }
`;

// const UploadCoverBtn = styled.input`
//   overflow: hidden;
//   margin: 20px;
//   padding: 2px;
//   height: 22px;
//   width: 100px;
//   color: white;
//   background-color: #b3b6b7;
//   border: 1px solid black;
//   text-align: center;
//   font-size: 12px;
//   font-weight: bold;
//   &:hover {
//     cursor: pointer;
//   }
// `;

const UploadCoverBtn = styled.input`
  overflow: hidden;
  margin: 20px;
  padding: 2px;
  height: 22px;
  width: auto;
  text-align: center;
`;

const UploadBtn = styled.button`
  overflow: hidden;
  margin: 40px 15px 25px 133px;
  height: 32px;
  width: 120px;
  color: white;
  background-color: #67f17e;
  border-radius: 5px;
  border-radius: 15px;
  border: 1px solid #183a37;
  text-align: center;
  font-size: 18px;
  font-weight: bold;
  &:hover {
    cursor: pointer;
  }
`;

const AddBook = () => {
  const [bookList, setBookList] = useState([]);
  // const [title, setTitle] = useState("");
  // const [author, setAuthor] = useState("");
  const [formData, setFormData] = useState({
    title: "",
    author: "",
    genres: "",
    pages: "",
    price: "",
    synopsis: "",
    bookPDF: "",
    coverPDF: "",
  });
  const bookCollection = collection(db, "books");
  const bookCollectionRef = collection(db, "books");

  const [getBook, setBook] = useState("");
  const [getStatus, setStatus] = useState("To do");
  const [getTitle, setTitle] = useState("");
  const [getAuthor, setAuthor] = useState("");
  const [getGenres, setGenres] = useState("");
  const [getPages, setPages] = useState("");
  const [getPrice, setPrice] = useState("");
  const [getSynopsis, setSynopsis] = useState("");
  const [getBookPDF, setBookPDF] = useState("");
  const [getCoverPDF, setCoverPDF] = useState("");

  const addBook = async (e) => {
    console.log("hallo");
    e.preventDefault();
    db.settings({
      timestampsInSnapshots: true,
    });
    const bookRef = db
      .collection("books")
      .add({
        title: getTitle,
        author: getAuthor,
        genres: getGenres,
        pages: Number(getPages),
        price: Number(getPrice),
        synopsis: getSynopsis,
        bookPDF: File(getBookPDF),
        coverPDF: File(getCoverPDF),
      })
      .then(function (docRef) {
        console.log("Tutorial created with ID: ", docRef.id);
      })
      .catch(function (error) {
        console.error("Error adding Tutorial: ", error);
      });
    this.setState({
      fullname: "",
      email: "",
    });

    const newCollectionRef = collection(
      db,
      "books",
      document.id,
      "name of new subcollection"
    );

    await addDoc(newCollectionRef, {
      data: "Hello World!",
    });
  };

  useEffect(() => {
    const getBooks = async () => {
      const data = await getDocs(bookCollection);
      setBookList(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
    };
    getBooks();
  }, []);

  const handleFormChange = (e) => {
    const newFormData = { ...formData, [e.target.name]: e.target.value };

    setFormData(newFormData);
  };

  const { title, author, genres, pages, price, synopsis, bookPDF } = formData;

  return (
    <Site>
      <Header />
      <Container>
        <TextContainer>Add Book</TextContainer>
        <ContainerItem>
          <TextContainerItem>Title:</TextContainerItem>
          <FormBlock
            type="text"
            name="title"
            placeholder=""
            value={title}
            onChange={handleFormChange}
          />
        </ContainerItem>
        <ContainerItem>
          <TextContainerItem>Author:</TextContainerItem>
          <FormBlock
            type="text"
            name="author"
            placeholder=""
            value={author}
            onChange={handleFormChange}
          />
        </ContainerItem>
        <ContainerItem>
          <TextContainerItem>Price:</TextContainerItem>
          <FormBlock
            type="number"
            name="price"
            placeholder=""
            value={price}
            onChange={handleFormChange}
          />
        </ContainerItem>
        <ContainerItem>
          <TextContainerItem>Genre(s):</TextContainerItem>
          <FormBlock
            type="text"
            name="genres"
            placeholder=""
            value={genres}
            onChange={handleFormChange}
          />
        </ContainerItem>
        <ContainerItem>
          <TextContainerItem>Size:</TextContainerItem>
          <FormBlock
            type="number"
            name="pages"
            placeholder=""
            value={pages}
            onChange={handleFormChange}
          />
        </ContainerItem>
        <ContainerItem>
          <TextContainerItem>Synopsis:</TextContainerItem>
          <TextAreaBlock>
            <textarea
              name="synopsis"
              rows="8"
              cols="29"
              placeholder=""
              value={synopsis}
              onChange={handleFormChange}
            ></textarea>
          </TextAreaBlock>
        </ContainerItem>
        {/* <ContainerItem> 
          <TextContainerItem>Cover:</TextContainerItem>
          <UploadCoverBtn type="file" />
        </ContainerItem>*/}
        <ContainerItem>
          <TextContainerItem>Book PDF:</TextContainerItem>
          <UploadCoverBtn type="file" />
        </ContainerItem>
        <ContainerItem>
          <TextContainerItem>Book PNG:</TextContainerItem>
          <UploadCoverBtn type="file" />
        </ContainerItem>
        <UploadBtn type="button" name="upload" onSubmit={addBook}>
          Upload
        </UploadBtn>
      </Container>
      <Footer />
    </Site>
  );
};

export default AddBook;
