import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const HeaderContainer = styled.nav`
  display: flex;
  width: 100%;
  height: 15vh;
  background: #5438dc;
  border: 1px #302080;
  border-style: hidden hidden groove hidden;
  > * {
    font-family: "HP Simplified";
  }
`;

const LogoSite = styled.text`
  text-align: center;
  width: 8vw;
  height: auto;
  padding: 5px;
  color: white;
  font-size: 2em;
`;

const HeaderBtn = styled.button`
  height: 2em;
  width: 4em;
  margin: 20px;
  font-size: 22px;
  font-weight: bold;
  color: white;
  background-color: red;
  &: hover {
    cursor: pointer;
  }
`;

const Header = () => {
  return (
    <HeaderContainer>
      <LogoSite>THE CAGE</LogoSite>
      <HeaderBtn>Series</HeaderBtn>
    </HeaderContainer>
  );
};

export default Header;
