import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const HeaderContainer = styled.nav`
  display: flex;
  width: 100%;
  height: 5.5rem;
  background: #5438dc;
  border: 1px #302080;
  border-style: hidden hidden groove hidden;
  > * {
    font-family: "HP Simplified";
  }
`;

const LogoSite = styled.text`
  text-align: center;
  width: 3em;
  height: auto;
  padding: 5px;
  color: white;
  font-size: 2em;
`;

const HeaderBtn = styled.button`
  height: 2em;
  width: 4em;
  margin: 20px;
  font-size: 22px;
  color: white;
  background-color: transparent;
  border: none;
  &: hover {
    cursor: pointer;
  }
`;

const UploadBtn = styled.button`
  height: 1.75em;
  width: 4.75em;
  margin: 25px;
  font-size: 22px;
  font-weight: bold;
  color: #5438dc;
  background-colour: white;
  border-radius: 10em;
  border: 2px solid #302080;
  &: hover {
    cursor: pointer;
  }
`;

const AccountBtn = styled.image`
  background-color: grey;
`;

const Header = () => {
  return (
    <HeaderContainer>
      <LogoSite>THE CAGE</LogoSite>
      <HeaderBtn>Series</HeaderBtn>
      <HeaderBtn>Manga</HeaderBtn>
      <HeaderBtn>Art</HeaderBtn>
      <UploadBtn>Upload</UploadBtn>
    </HeaderContainer>
  );
};

export default Header;
