import React from "react";
import styled from "styled-components";

const SidebarContainer = styled.div`
  width: 100xp;
  height: auto;
  float: left;
  z-index: 10;
  display: grid;
  column-row-start: 1;
  column-rwo-end: 7;
  color: grey;
  background: rgb(156, 141, 143);
`;

const SidebarContents = styled.div`
  width: auto;
  height: auto;
  margin: 30px 40px;
`;

const SidebarLinks = styled.div`
  width: auto;
  height: auto;
  text-align: center;
  font-size: 18px;
  color: white;
  text-decoration: none;
  border: none;
  -webkit-text-stroke: 0.4px black;
  &:hover {
    cursor: pointer;
    color: #22d55e;
    -webkit-text-stroke: 0.4px #567568;
  }
`;

const Sidebar = () => {
  return (
    <SidebarContainer>
      <SidebarContents>
        <SidebarLinks>About us</SidebarLinks>
      </SidebarContents>
      <SidebarContents>
        <SidebarLinks>Books</SidebarLinks>
      </SidebarContents>
    </SidebarContainer>
  );
};

export default Sidebar;
