import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { initializeApp } from "firebase/app";
import { getAuth, GoogleAuthProvider } from "firebase/auth";
import { getFirestore, getDocs, collection, doc } from "firebase/firestore";
import Header from "./components/layout/Header";
import Footer from "./components/layout/Footer";

const Site = styled.div`
  width: auto;
  height: auto;
  background: #fffcf6;
`;

const Container = styled.div`
  width: auto;
  height: auto;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  column-gap: 30px;
`;

const Cover = styled.img`
  width: 400px;
  height: 500px;
  margin: 30px;
  background-color: black;
`;

const BookDetails = styled.div`
  width: 400px;
  height: 500px;
  margin: 30px;
  background: rgb(204, 221, 211);
  background: linear-gradient(
    0deg,
    rgba(168, 193, 179, 0.8) 0%,
    rgba(204, 221, 211, 1) 100%
  );
  border-radius: 15px;
  border: 1.5px solid #88665d;
`;

const HeaderTextDetails = styled.div`
  margin: 0px 0px 15px 15px;
  text-align: left;
  font-size: 24px;
  font-weight: bold;
  color: white;
  -webkit-text-stroke: 0.75px #c4be9f;
`;

const TextDetails = styled.div`
  margin: 0px 0px 0px 0px;
  text-align: left;
  font-size: 17px;
  color: white;
  -webkit-text-stroke: 0.75px #c4be9f;
`;

const ShowBook = () => {
  // const [reasonList, setReasonList] = useState([]);
  // const reasonCollection = collection(db, "Reason");

  // useEffect(() => {
  //   const getReason = async () => {
  //     const data = await getDocs(reasonCollection);
  //     setReasonList(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
  //     console.log(data);
  //   };
  //   getReason();
  // }, []);

  return (
    <Site>
      <Header />
      <Container>
        <Cover></Cover>
        <BookDetails>
          {/* TITLE */}
          <HeaderTextDetails>
            TITLE:
            <TextDetails>Reason</TextDetails>
          </HeaderTextDetails>
          {/* AUTHOR */}
          <HeaderTextDetails>
            AUTHOR:
            <TextDetails>Tom Kooij</TextDetails>
          </HeaderTextDetails>
          {/* PRICE */}
          <HeaderTextDetails>
            PRICE:
            <TextDetails>â‚¬8,99</TextDetails>
          </HeaderTextDetails>
          {/* GENRES */}
          <HeaderTextDetails>
            GENRE(S):
            <TextDetails>Fantasy</TextDetails>
          </HeaderTextDetails>
          {/* PAGES */}
          <HeaderTextDetails>
            PAGES:
            <TextDetails>258</TextDetails>
          </HeaderTextDetails>
          {/* SYNOPSIS */}
          <HeaderTextDetails>
            SYNOPSIS:
            <TextDetails>"Hallo"</TextDetails>
          </HeaderTextDetails>
        </BookDetails>
      </Container>
      <Footer />
    </Site>
  );
};

export default ShowBook;
