import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const HeaderContainer = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  height: 5.5rem;
  background: #5438dc;
  border: 1px #302080;
  border-style: hidden hidden groove hidden;
  > * {
    font-family: "HP Simplified";
  }
`;

const BtnCollection = styled.div`
  display: flex;
  align-items: center;
  width: auto;
  height: auto;
  column-gap: 5px;
`;

const LogoSite = styled.text`
  text-align: center;
  width: 3em;
  height: auto;
  padding: 5px;
  color: white;
  font-size: 2em;
`;

const HeaderBtn = styled.button`
  position: relative;
  display: flex;
  height: 2em;
  width: 3em;
  margin: 10px;
  font-size: 22px;
  font-family: "HP Simplified";
  color: white;
  background-color: transparent;
  border: none;
  &: hover {
    cursor: pointer;
  }
`;

const UploadBtn = styled.button`
  position: relative;
  display: flex;
  height: 1.75em;
  width: 4.75em;
  margin: 10px;
  font-size: 22px;
  font-weight: bold;
  color: #5438dc;
  background-colour: white;
  border-radius: 10em;
  border: 2px solid #302080;
  &: hover {
    cursor: pointer;
  }
`;

const AccountBtn = styled.image`
  position: relative;
  display: flex;
  width: 3.5rem;
  height: 3.5rem;
  margin: 10px;
  background-color: white;
  border-radius: 5rem;
  border: 2px solid #302080;
  &: hover {
    cursor: pointer;
  }
`;

const Header = () => {
  return (
    <HeaderContainer>
      <LogoSite>THE CAGE</LogoSite>
      <BtnCollection>
        <HeaderBtn>Novels</HeaderBtn>
        <HeaderBtn>Manga</HeaderBtn>
        <HeaderBtn>Art</HeaderBtn>
        <UploadBtn>Upload</UploadBtn>
        <AccountBtn></AccountBtn>
      </BtnCollection>
    </HeaderContainer>
  );
};

export default Header;
