import React, { useEffect, useState } from "react";
import styled from "styled-components";
import {
  getFirestore,
  getDocs,
  collection,
  doc,
  deleteDoc,
} from "firebase/firestore";
import { Link } from "react-router-dom";
//import { db } from "./firebaseconfig";
import Header from "./components/layout/Header";
/*import Footer from "./components/layout/Footer";
import deleteItem from "./img/UI/remove.png";
import updateItem from "./img/UI/update.png";*/

const Site = styled.html`
  width: auto;
  height: auto;
`;

const Collection = styled.div`
  width: auto;
  height: auto;
  margin: 0px 50px 0px 50px;
`;

const Label = styled.text`
  display: flex;
  margin: 20px 0px 0px 30px;
  font-size: 42px;
  font-family: "HP Simplified";
  color: #5438dc;
`;

const ItemCollection = styled.div`
  display: inline-flex;
  width: auto;
  height: auto;
  column-gap: 10px;
  margin: 10px;
`;

const Item = styled.div`
  display: flex;
  margin: 15px 0px 0px 30px;
  width: 15em;
  height: 20em;
  background-color: white;
  border: 1.5px solid #302080;
`;

const ItemTxtBox = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 246px 0px 0px 0px;
  width: 15em;
  height: 4em;
  color: white;
  background-color: rgba(1, 1, 1, 0.3);
  font-size: 18px;
`;

const Home = () => {
  return (
    <Site>
      <Header />
      <Collection>
        <Label>New Novels:</Label>
        <ItemCollection>
          <Link to="/ShowBook">
            <Item>
              <ItemTxtBox>text</ItemTxtBox>
            </Item>
          </Link>
        </ItemCollection>
      </Collection>
      <Collection>
        <Label>New Manga:</Label>
        <ItemCollection>
          <Item>
            <ItemTxtBox>text</ItemTxtBox>
          </Item>
        </ItemCollection>
      </Collection>
      <Collection>
        <Label>New Art:</Label>
        <ItemCollection>
          <Item>
            <ItemTxtBox>text</ItemTxtBox>
          </Item>
        </ItemCollection>
      </Collection>
    </Site>
  );
};

export default Home;
