import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { initializeApp } from "firebase/app";
// import { getAuth, GoogleAuthProvider } from "firebase/auth";
import { getFirestore, getDocs, collection, doc } from "firebase/firestore";
import Header from "./components/layout/Header";
import Footer from "./components/layout/Footer";

const firebaseConfig = {
  apiKey: "AIzaSyD-qFrqQQtmm6nKoOQI0Ppd99t_o_xcA6Y",
  authDomain: "node-sql-ebb4d.firebaseapp.com",
  databaseURL: "",
  projectId: "node-sql-ebb4d",
  storageBucket: "node-sql-ebb4d.appspot.com",
  messagingSenderId: "926508543396",
  appId: "1:926508543396:web:1fbe34f55ee5a961d6e531",
};

//Initialize firebase

const app = initializeApp(firebaseConfig);
export const db = getFirestore(app);

const LoginContainer = styled.div`
  width: auto;
  height: auto;
  background: #fffcf6;
`;

const Container = styled.div`
  margin: 5vh 5vh 5vw 35vw;
  width: 400px;
  height: 320px;
  background: rgb(204, 221, 211);
  background: linear-gradient(
    0deg,
    rgba(168, 193, 179, 0.8) 0%,
    rgba(204, 221, 211, 1) 100%
  );
  border-radius: 15px;
  border: 1.5px solid #88665d;
`;

const TextContainer = styled.div`
  margin: 15px;
  text-align: center;
  font-size: 48px;
  font-weight: bold;
  color: white;
  -webkit-text-stroke: 0.75px #c4be9f;
`;

const FormBlock = styled.input`
  position: center;
  overflow: hidden;
  padding-left: 3px;
  padding-top: 3px;
  margin: 25px 15px 25px 40px;
  height: 32px;
  width: 300px;
  color: grey;
  background-color: white;
  border: 1px solid grey;
  border-radius: 15px;
  &:hover {
    cursor: text;
  }
`;

const LoginBtn = styled.div`
  overflow: hidden;
  margin: 10px 15px 25px 133px;
  height: 32px;
  width: 120px;
  color: white;
  background-color: #67f17e;
  border-radius: 5px;
  border-radius: 15px;
  border: 1px solid #183a37;
  text-align: center;
  font-size: 18px;
  font-weight: bold;
  &:hover {
    cursor: pointer;
  }
`;

const Login = () => {
  const [loginData, setLoginData] = useState({
    email: "",
    password: "",
  });

  const handleFormChange = (e) => {
    const newLoginData = { ...loginData, [e.target.name]: e.target.value };
    setLoginData(newLoginData);
  };
  const { email, password } = loginData;

  return (
    <LoginContainer>
      <div className="siteLogin">
        <Header />
        <Container>
          <TextContainer>Login</TextContainer>
          <FormBlock
            type="email"
            name="email"
            placeholder="Email..."
            value={email}
            onChange={handleFormChange}
          ></FormBlock>
          <FormBlock
            type="password"
            name="password"
            placeholder="Password..."
            value={password}
            onChange={handleFormChange}
          ></FormBlock>
          <LoginBtn>Log In</LoginBtn>
        </Container>
        <Footer />
      </div>
    </LoginContainer>
  );
};

export default Login;
