  //   const getReason = async () => {
  //     const data = await getDocs(reasonCollection);
  //     setReasonList(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
  //     console.log(data);
  //   };
  //   getReason();
  // }, []);

  return (
    <Site>
      <Header />
      <Container>
        <Cover></Cover>
        <BookDetails>
          <TextDetails>Title:</TextDetails>
        </BookDetails>
      </Container>
      <Footer />
    </Site>
  );
};

export default ShowBook;
