import React from "react";
import styled from "styled-components";

const HeaderContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  height: 80px;
  padding: 0px 30px;
  background: rgb(156, 141, 143);
  background: linear-gradient(
    0deg,
    rgba(156, 141, 143, 1) 0%,
    rgba(132, 117, 119, 1) 100%
  );
`;

const Navigatie = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  column-gap: 40px;
  width: auto;
  height: 100%;
  padding: 0px 10px;
`;

const Logo = styled.div``;

const ButtonsHeader = styled.div`
  display: flex;
`;

const ButtonHeader = styled.button`
  display: inline-block;
  height: 40px;
  width: 100px;
  margin: 10px;
  text-align: center;
  font-size: 20px;
  color: black;
  text-decoration: none;
  background-color: rgba(0, 0, 0, 0);
  border: none;
  &:hover {
    cursor: pointer;
    color: #20bf55;
  }
`;

const Searchbar = styled.div`
  overflow: hidden;
  padding-left: 3px;
  height: 25px;
  width: 200px;
  background: white;
  border: 1px solid grey;
  border-radius: 15px;
  &:hover {
    cursor: text;
  }
`;

const AccountButton = styled.button`
  display: inline-box;
  background: grey;
  height: 50px;
  width: 50px;
  border: 1px solid black;
  &:hover {
    cursor: pointer;
    background: #37505c;
  }
`;

const Header = () => {
  return (
    <HeaderContainer>
      <Logo>
        <div className="header_image_site">logo</div>
      </Logo>
      <Navigatie>
        <ButtonsHeader>
          <ButtonHeader>Novels</ButtonHeader>
          <ButtonHeader>Art</ButtonHeader>
        </ButtonsHeader>
        <Searchbar>
          <div className="searchbar_header">Search...</div>
        </Searchbar>
        <AccountButton>
          <div className="account_button"></div>
        </AccountButton>
      </Navigatie>
    </HeaderContainer>
  );
};

export default Header;
