import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { initializeApp } from "firebase/app";
import { getAuth, GoogleAuthProvider } from "firebase/auth";
import { getFirestore, getDocs, collection, doc } from "firebase/firestore";
import Header from "./components/layout/Header";
import Footer from "./components/layout/Footer";

const firebaseConfig = {
  apiKey: "AIzaSyD-qFrqQQtmm6nKoOQI0Ppd99t_o_xcA6Y",
  authDomain: "node-sql-ebb4d.firebaseapp.com",
  databaseURL: "",
  projectId: "node-sql-ebb4d",
  storageBucket: "node-sql-ebb4d.appspot.com",
  messagingSenderId: "926508543396",
  appId: "1:926508543396:web:1fbe34f55ee5a961d6e531",
};

//Initialize firebase

const app = initializeApp(firebaseConfig);
export const db = getFirestore(app);

const Site = styled.div`
  width: auto;
  height: auto;
  background: #fffcf6;
`;

const TextContainer = styled.div`
  text-align: center;
  font-size: 48px;
  font-weight: bold;
  color: white;
  -webkit-text-stroke: 0.75px #c4be9f;
`;

const Container = styled.div`
  display: grid;
  width: auto;
  height: auto;
`;

const ContainerItem = styled.div``;

const ContainerItemPct = styled.div`
  margin: 0px 0px 0px 30px;
  width: 250px;
  height: 300px;
  background-color: red;
  border: 1.5px #88665d;
  border-style: groove groove hidden groove;
`;

const ContainerItemTxt = styled.div`
  margin: 0px 0px 30px 30px;
  width: 250px;
  background-color: red;
  border: 1.5px #88665d;
  border-style: hidden groove groove groove;
  text-align: center;
  font-size: 20px;
`;

const Home = () => {
  const [getBookList, setBookList] = useState([]);
  const bookCollection = collection(db, "books");

  useEffect(() => {
    const getBooks = async () => {
      const data = await getDocs(bookCollection);
      setBookList(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
    };
    getBooks();
  }, []);

  return (
    <Site>
      <Header />
      <TextContainer>New</TextContainer>
      <Container>
        <ContainerItem>
          <ContainerItemPct></ContainerItemPct>
          <ContainerItemTxt>Hallo</ContainerItemTxt>
        </ContainerItem>
      </Container>
      <Footer />
    </Site>
  );
};

export default Home;
